import AwesomeSlider from "react-awesome-slider";
import "react-awesome-slider/dist/styles.css";
import 'react-awesome-slider/dist/custom-animations/cube-animation.css';
import bojack from '../assests/images/series/bojack-3.jpg'

export default function Awesome() {
  const bgImg = {
    position: "absolute",
    zIndex: -1,
    left: 0,
    top: 0,
    width: "100%"
  };

  const contentStyle = {
    color: "black",
    fontSize: "20px"
  };

  const data = [
    {
      id: 
        "1",
      icon:
       {bojack},
      title: 
        "Web Design",
      desc:
        "Lorem Ipsum is simply dummy text of the printing and typesetting industry. ",
      img:
      //{bojack}
      //"assests/images/series/bojack-3.jpg"
      //    "/Users/ghost/Developer/react/govlink/src/assests/images/series/stranger-things-5.jpg"
        "https://99designs-blog.imgix.net/blog/wp-content/uploads/2018/10/attachment_100040756-e1538485934255.jpeg?auto=format&q=60&fit=max&w=930"
    },
    {
      id: 
        "2",
      icon: 
        "asset/globe.png",
      title: 
        "Mobile Application",
      desc:
        "Lorem Ipsum is simply dummy text of the printing and typesetting industry.",
      img:
        "https://i.pinimg.com/originals/e9/c9/2f/e9c92f7869d682a6fa5a97fb8a298f30.jpg"
    },
    {
      id: 
        "3",
      icon: 
        "asset/writing.png",
      title: 
        "Branding",
      desc:
        "Lorem Ipsum is simply dummy text of the printing and typesetting industry.",
      img:
        "https://i.pinimg.com/originals/a9/f6/94/a9f69465d972a004ad581f245d6ad581.jpg"
    }
  ];
  return (
    <div className="App">
      <AwesomeSlider animation="cubeAnimation" >
        {data.map((d) => (
       <div className="item" style={{ zIndex: 2 }}>
            <div className="left">
              <div className="leftContainer">
              <h2>{d.title}</h2>
                <p>{d.desc} </p>

                <div className="imgContainer">
                  <img src={d.img} alt="slide" style={bgImg}/>
                </div>

              </div>
            </div>

          </div>
        ))}
      </AwesomeSlider>
    </div>
  );
}
