import AwesomeSlider from "react-awesome-slider";
import "react-awesome-slider/dist/styles.css";
import 'react-awesome-slider/dist/custom-animations/cube-animation.css';
//import Tilt from '../../src';
import Tilt from 'react-parallax-tilt';
//import imgNyc from './img/nyc.jpg';
import withAutoplay from 'react-awesome-slider/dist/autoplay';

import withCaption from 'react-awesome-slider/dist/captioned';
import 'react-awesome-slider/dist/styles.css';
import 'react-awesome-slider/dist/captioned.css';



export default function Awesome() {
  const CaptionedSlider = withCaption(AwesomeSlider);

  const bgImg = {
    position: "absolute",
    zIndex: -1,
    left: 0,
    top: 0,
    width: "100%"
  };

  const contentStyle = {
    color: "black",
    fontSize: "50px"
  };

  const data = [
    {
      id: 
        "1",
      icon:
      "https://coinmac.org/static/img/digital-market.jpg",
      title: 
        "Digital Marketting",
      desc:
        "Lorem Ipsum is simply dummy text of the printing and typesetting industry. ",
      img: 
      "https://coinmac.org/static/img/digital-market.jpg"
    },
    {
      id: 
        "2",
      icon: 
        "asset/globe.png",
      title: 
        "Mobile Application",
      desc:
        "Lorem Ipsum is simply dummy text of the printing and typesetting industry.",
      img:
        "https://i.pinimg.com/originals/e9/c9/2f/e9c92f7869d682a6fa5a97fb8a298f30.jpg"
    },
    {
      id: 
        "3",
      icon: 
        "asset/writing.png",
      title: 
        "Branding",
      desc:
        "Lorem Ipsum is simply dummy text of the printing and typesetting industry.",
      img:
        "https://i.pinimg.com/originals/a9/f6/94/a9f69465d972a004ad581f245d6ad581.jpg"
    }
  ];
  return (
    <Tilt
    className="tilt-img"
    tiltMaxAngleX={35}
    tiltMaxAngleY={35}
    perspective={900}
    scale={1.1}
    transitionSpeed={2000}
    gyroscope={true}
    >

    <div className="App">

    <CaptionedSlider

    cssModule={CaptionedStyles}   
     >
        {data.map((d) => (
       <div className="item" style={{ zIndex: 2 }}>
            <div className="left">
              <div className="leftContainer">
              <h2 style={ contentStyle }>{d.title}</h2>
                <p>{d.desc} </p>

                <div className="imgContainer">
                  <img src={d.img} alt="slide" style={bgImg}/>
                </div>

              </div>
            </div>

          </div>
        ))}
      </CaptionedSlider>
    </div>
    </Tilt>

  );
}
